mapper.select:
		select `id`,`user_id`,`collectreport_id`,`report_type`,`collectuser_id`,`collectuser_name` from design_collect

		  		   and id = #{id} 
		  		   and user_id = #{userId} 
		  		   and collectreport_id = #{collectreportId} 
		  		   and report_type = #{reportType} 
		  		   and collectuser_id = #{collectuserId} 
		  		   and collectuser_name = #{collectuserName} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from design_collect

  		   and id = #{id} 
  		   and user_id = #{userId} 
  		   and collectreport_id = #{collectreportId} 
  		   and report_type = #{reportType} 
  		   and collectuser_id = #{collectuserId} 
  		   and collectuser_name = #{collectuserName}
